
public class PostIncrement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=1;
		a++;
		System.out.println(a);
		float b=2.25f;
		b++;
		System.out.println(b);
		char c='a';
		c++;
		System.out.println(c);
		boolean d=true;
		//d++;
		System.out.println(d);
		double e=3.278;
		e++;
		System.out.println(e);

	}

}
