
public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='e';
		switch (ch) {
		case 'a':
			System.out.println("It's a Vowel");
			break;
		case 'e':
			System.out.println("It's a Vowel");
			break;
		case 'i':
			System.out.println("It's a Vowel");
			break;
		case 'o':
			System.out.println("It's a Vowel");
			break;
		case 'u':
			System.out.println("It's a Vowel");
			break;
		default:
			System.out.println("It's a Consonant");
			break;
		}

	}

}
